actionConfiguration.body:
mutation AddAssignment(
  $startDate: timestamp, 
  $endDate: timestamp,
  $positionFestCloudId: String!, 
  $workGroupFestCloudId: String!,
  $employeeFestCloudId: String!, 
	$assignmentcreator: String!, 
) {
  inserted:insert_people_assignmentext_v0(objects: {
    Stage: "PendingSubstitution", 
		Type: "Secondary",
		AssignmentCreatorFestCloudID: $assignmentcreator,
    assignmentext_festcloudid_assignment: {
      data: {
        EndDate: $endDate,
        StartDate: $startDate, 
        PositionFestCloudID: $positionFestCloudId, 
        WorkgroupFestCloudID: $workGroupFestCloudId, 
        EmployeeFestCloudID: $employeeFestCloudId, 
      }
    }
  }) {
    affected_rows
    returning {
      FestCloudID
    }
  }
}

pluginSpecifiedTemplates.value:
{
	startDate: {{moment(SubstitutionStartDatePicker.selectedDate)}},
	endDate: {{moment(SubstitutionEndDatePicker.selectedDate)}},
	positionFestCloudId: {{positionSelectAssign.selectedOptionValue}},
	workGroupFestCloudId: {{workgroupSelectAssign.selectedOptionValue}},
	employeeFestCloudId: {{init.employeeFestCloudID}},
	assignmentcreator: {{appsmith.store.myFestCloudId}}
}
