@charset "UTF-8";
html, body {
  margin: 0;
  padding: 0;
  overflow: hidden !important;
  height: 100%;
  width: 100%;
}

/* Основний контейнер Appsmith */
.app {
  height: 938px;
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-start;
  padding: 0;
  overflow: hidden !important;
  scrollbar-width: none !important;
}

.app::-webkit-scrollbar {
  display: none !important;
  width: 0 !important;
  height: 0 !important;
}

.app svg {
  width: 99%;
}

