srcDoc.css:
@charset "UTF-8";
html, body {
  margin: 0;
  padding: 0;
  overflow: hidden !important;
  height: 100%;
  width: 100%;
}

/* Основний контейнер Appsmith */
.app {
  height: 938px;
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-end;
  padding: 0;
  overflow: hidden !important;
  scrollbar-width: none !important;
}

.app::-webkit-scrollbar {
  display: none !important;
  width: 0 !important;
  height: 0 !important;
}

.app svg {
  width: 100%;
}

.app .selected {
  fill: linear-gradient(270deg, #EEF4FF -5.06%, #F9F9F9 100%);
  stroke-width: 1px;
  stroke: #5D9AFF;
}

